create table agendamento (fk_agenda integer, fk_cliente integer, fk_servico integer, id_agendamento integer not null auto_increment, data_hora datetime(6), status_agendamento enum ('AGENDADO','CANCELADO','PENDENTE','REALIZADO'), primary key (id_agendamento)) engine=InnoDB;
