create table imagem (fk_empresa integer, id_imagem integer not null auto_increment, url_imagem varchar(255), primary key (id_imagem)) engine=InnoDB;
create table notificacao (id_notificacao integer not null auto_increment, mensagem_agendamento varchar(255), mensagem_cancelamento varchar(255), primary key (id_notificacao)) engine=InnoDB;
create table servico (duracao time(6), fk_empresa integer, id_servico integer not null auto_increment, valor_servico float(53) not null, nome_servico varchar(100) not null, cor_referencia_hex varchar(255), descricao varchar(255) not null, status_atividade enum ('ATIVO','INATIVO'), primary key (id_servico)) engine=InnoDB;
create table usuarios (data_criacao date, fk_empresa integer, fk_endereco integer, fk_funcao integer, id_pessoa integer not null auto_increment, representante bit, tipos_de_usuario tinyint, cpf varchar(255), email_pessoa varchar(255) not null, firebase_uid varchar(255), nome_pessoa varchar(255) not null, numero_telefone varchar(255), senha varchar(255), status_atividade enum ('ATIVO','INATIVO'), primary key (id_pessoa)) engine=InnoDB;
